SELECT id, name, initialBalance FROM stockUsers WHERE name = '张三';

-- 查询张三的授权记录
SELECT * FROM stockUserPermissions WHERE stockUserId = (SELECT id FROM stockUsers WHERE name = '张三' LIMIT 1);

-- 查询张三的所有余额记录
SELECT date, balance FROM stockBalances WHERE stockUserId = (SELECT id FROM stockUsers WHERE name = '张三' LIMIT 1) ORDER BY date ASC;

